const stargazersURL = `https://api.github.com/repos/${components.autocomplete1.text}/stargazers?per_page=100&page={page}`;

const totalPages = Math.ceil( parseFloat(queries.获取仓库基本信息.data.starGazers/100));

let pages = [...Array(totalPages).keys()];
console.log(pages)

let dates = {};
let results = [];

let headers = {
  'Accept': 'application/vnd.github.v3.star+json'
}

if(components.textinput2.value !== '') { 
  headers['Authorization'] = `token ${components.textinput2.value}`
}

for(const page of pages) {
  const url = stargazersURL.replace('{page}', page + 1);
  const data = (await axios.get(url, { headers })).data;
	console.log(page,totalPages,parseInt(((pages+1)/totalPages)*100))
  components.progress1.setPrograss( parseInt(((page+1)/totalPages)*100) )
  for(star of data) {
    const starredAt = moment(star.starred_at).endOf('day').format('yyyy-MM-DD');
    dates[starredAt] = (dates[starredAt] || 0) + 1;
  }

}

let total = 0;
for (date of Object.keys(dates)) {
    total = total + dates[date];
    results.push({ x: date, y: total});
}

return results;

